import itertools
for i, val in zip(range(3), itertools.cycle('AB')):
    print(val, end=' ')

from itertools import count
for i in count(3, 2):
    if i > 9:
        break
    print(i, end=' ')

from functools import lru_cache
@lru_cache(maxsize=None)
def fib(n):
    if n < 2: return n
    return fib(n-1) + fib(n-2)
print(fib(5))

